mapper.update:
/*savePasswordFailCnt 비밀번호 입력 실패 횟수 저장  */
UPDATE logininfo 
   SET login_failed_count = ISNULL(login_failed_count,0) + 1 
     , islocked = #{lockAccount}
 WHERE memcd = #{strRSMEMCD}

mapper.delete:
 /* deleteOdCart */
DELETE FROM od_cart 
        WHERE memcd = #{strRSMEMCD}

 /* deleteAfterSevenDays 담은지 7일이 넘었거나 판매중지인 상품 정리*/
DELETE FROM shopping_basket 
        WHERE memcd = #{strRSMEMCD}

          AND od_gubun <> '12'

          AND gdcd IN (SELECT c.gdcd 
                       FROM shopping_basket c
                         JOIN gd_master g
                           ON g.gdcd = c.gdcd
                        WHERE memcd = #{strRSMEMCD}
                          AND (DATEDIFF(d,c.indt,GETDATE()) > 7 OR g.useyn = 'N') 
                      )

/* deleteReserveProdStopSelling 담은 예약주문 중, 현재 판매하지 않는 상품 정리*/ DELETE FROM shopping_basket WHERE memcd = #{strRSMEMCD} AND od_gubun = '12' AND gdcd NOT IN ( SELECT gdcd FROM od_reservegoods
WHERE DATEDIFF(d,od_from,GETDATE()) >= 0 AND DATEDIFF(d,GETDATE(),od_to) >= 0
AND useyn = 'Y' )

mapper.update:
/* unlockCartProd 잠겨 있는 장바구니 상품을 해제*/
UPDATE shopping_basket
   SET freeze = '0'
 WHERE memcd = #{strRSMEMCD}
   AND freeze = '1'

mapper.insert:
/* 로그인 History 테이블 저장 */
INSERT INTO logininfo2(
                        memcd
                      , ipx
                      , refererx
                      , device
                      , user_agent
                      ) 
       VALUES (
                #{strRSMEMCD}
              , #{ipx}
              , #{refererx}
              , 'M'
              , #{user_agent}
              )

mapper.select:
/* checkTwalkAccountInfo 외부 연계접속(T-walk 체크*/
 SELECT COUNT(1) RCNT
 FROM TWALK_MEMBER
  WHERE tw_memcd = #{strRSMEMCD}
    AND tw_pid = #{pid}

mapper.insert:
/*  saveTwalkAccountInfo 외부 연계접속(T-walk)이 있을 경우 연동정보 등록*/
INSERT INTO TWALK_MEMBER(
                          tw_memcd
                        , tw_pid
                        , tw_indt
                        ) 
       VALUES (
                #{strRSMEMCD}
              , #{pid}
              , getdate()
              )

mapper.select:
/* loginPointAutoGrant 로그인 포인트 자동 부여*/ SELECT TOP 1 idx , ISNULL(points,0) points , memo FROM book_accpoint WHERE used = 'N' AND memcd = #{strRSMEMCD}
AND ISNULL(apydt,getdate()) >= CONVERT(VARCHAR,GETDATE(),23)
ORDER BY apydt DESC

mapper.update:
/* pointGrantWithAuthority 포인트 권한적립*/
UPDATE book_accpoint 
   SET used = 'Y'
     , usedt = getdate()
 WHERE idx = #{rsIdx}
