mapper.select:
SELECT g.gdcd , g.gdname , g.gdimg , g.gdimg2 , g.gdimg3 , g.gdimg4 , g.mgdimg1 , g.dispatchtype , g.priceyn , ISNULL(g.qtysaleyn,'N') qtysaleyn , g.price1 , dbo.getMasterPrice(g.gdcd) saleprice
, g.div1 , g.div2 , g.divcd , ISNULL(s.gdcnt,99999) gdcnt , g.minqty , g.maxqty , g.shortdesc , ISNULL(t.cdname,'미정') AS typename , t2.cdcode putclass_cd, t2.cdname putclass , ISNULL(g.recommendyn,'N') recommendyn , ISNULL(g.newyn,'N') newyn , ISNULL(g.deliverydtyn,'N') deliverydtyn , ISNULL(g.thedaysyn,'N') thedaysyn , ISNULL(g.soldoutyn,'N') soldoutyn , CONVERT(CHAR(10),g.plandate,120) plandate , ISNULL(g.reserveyn,'N') reserveyn , CONVERT(NVARCHAR(max),g.explain) AS explain , g.unit , i.address , i.origin , i.producer , ISNULL(r.od_from,'') od_from , ISNULL(r.od_to,'') od_to , ISNULL(r.dlv_from,'') dlv_from , ISNULL(r.dlv_to,'') dlv_to , CASE WHEN ISNULL(r.packunit,'') = '' THEN '' ELSE r.packunit END packunit , g.expdt , g.vat , g.limitdelivery , g.bestbeforedate , g.packaging , g.handling , g.precaution1 , g.precaution2 , g.importnote , g.addinformation , g.testidx , g.optionp , g.updt , g.subgdcd1 , g.subgdcd2 , g.subgdcd3 , g.subgdcd4 , g.subgdcd5 , g.exdiv1 , g.exdiv2 , ISNULL(t8.delpol,'01') delpol , ISNULL(t8.limamt,0) limamt , ISNULL(t8.delcharge,0) delcharge , ISNULL(t.cdname,'미지정') AS gradedesc , g.specifications , g.periodofuse , g.howtouse , g.scode , (g.price1 - dbo.getMasterPrice(g.gdcd)) / g.price1 * 100 AS discountrate FROM gd_master g LEFT JOIN gd_item i ON i.gdcd = g.gdcd LEFT JOIN cd_master t ON t.cdtype = '09' AND t.cdcode = i.typecd LEFT JOIN cd_master t2 ON t2.cdtype = '88' AND t2.cdcode = i.putclass LEFT JOIN gd_cnt s ON g.gdcd = s.gdcd LEFT JOIN supplus t8 ON g.scode = t8.suno LEFT JOIN od_reservegoods r ON g.gdcd = r.gdcd AND r.useyn = 'Y'
AND DATEDIFF(D, CONVERT(DATETIME,r.od_from,120),GETDATE()) >= 0 AND DATEDIFF(D, GETDATE(),CONVERT(DATETIME,r.od_to,120)) >= 0
WHERE 1 = 1 AND g.useyn = 'Y' AND g.gdcd = #{strGDCD}
AND g.divcd <> '10'
AND (g.reserveyn = 'N' OR (g.reserveyn = 'Y' AND r.gdcd IS NOT NULL)) AND (ISNULL(g.expgrcd, '') = '' OR EXISTS (SELECT cl.ulgrcd FROM customgrp_l cl WHERE cl.ulcode = g.expgrcd
AND cl.ulgrcd = #{strMEMGRPCD}
) )

SELECT a.gdqty
     , b.gdname 
    FROM ( SELECT sgdcd, gdqty
             FROM gd_week
              WHERE gdyear = #{strNowGDYear}
                AND gdweek = #{strNowGDWeek}
                AND gdcd = #{strGDCD}

                AND sgdcd <> '*'

         ) A
              JOIN gd_master b 
                ON a.sgdcd = b.gdcd 
   ORDER BY b.gdname

SELECT testpath
  FROM c_pesticide_residue
   WHERE testidx = #{testidx}

SELECT ppcode 
  FROM gd_pipn 
   WHERE ppartc = #{strGDCD}

SELECT CEILING( CAST(COUNT(1) AS FLOAT)/ #{intPagePerItem} ) MAXPAGE
     , COUNT(rseqno) CNT
  FROM productreview
   WHERE 1 = 1
     AND rblind = 'N'
     AND rid = #{reviewId}
     AND ridx = #{strGDCD}
     AND rdatex >= dateadd(M ,-6, getdate())
     AND rtype = #{reviewType}
