mapper.select:
/*bestSaleHeaderInfo 기획전 정보 헤더*/
SELECT *
FROM specialsellingh
WHERE shidx = #{sidx}

/* bestSaleDefaultCategory 기획전 상품 카테고리(기본)*/ SELECT cdcode , cdname , COUNT(x.slgdcd) CNT FROM specialsellingl x JOIN gd_master g ON x.slgdcd = g.gdcd JOIN cd_master c ON c.cdtype = '20' AND c.cdcode = g.div1 LEFT JOIN od_reservegoods r ON x.slgdcd = r.gdcd AND g.useyn = 'N' WHERE x.slidx = #{sidx} AND (g.useyn = 'Y' OR (g.useyn = 'N' AND r.useyn = 'Y' AND GETDATE() >= CONVERT(DATETIME, od_from, 21) AND GETDATE() < CONVERT(DATETIME, od_to, 21) + 1
) )
AND ISNULL(g.optionp,'N') = 'N'
GROUP BY cdcode, cdname ORDER BY cdcode

/* bestSaleDefaultCategory 기획전 상품 카테고리(사용자 정의)*/ SELECT x.slcat cdcode , COUNT(x.slcat) CNT FROM specialsellingl x JOIN gd_master g ON x.slgdcd = g.gdcd LEFT JOIN od_reservegoods r ON x.slgdcd = r.gdcd AND g.useyn = 'N' WHERE x.slidx = #{sidx}
AND ISNULL(g.optionp,'N') = 'N'
GROUP BY x.slcat ORDER BY x.slcat

 /*bestSaleToptenProd Top 10 상품 조회*/
SELECT TOP 10 shidx
     , shtitle
     , grpcd
FROM specialsellingh
 WHERE 1 = 1
   AND shrtype= #{shrtype}

   AND shstartdt <= GETDATE()
   AND shenddt >= GETDATE()



        AND shlevel = #{rsshlevel}


        AND shlevel = #{rsshlevel}
        AND shrelidx = #{rsshrelidx}



   AND ISNULL(shhideyn, 'N') <> 'Y'

ORDER BY ISNULL(grpcd,''), shidx DESC

/*bestSaleProdInfo 기획전 포함 상품 정보 */ SELECT * FROM ( SELECT '11' odtype , a.gdcd , a.gdname , a.unit , a.price1 , a.gdimg , a.mgdimg1 , a.divcd , a.div1 , a.indt , a.updt , a.shortdesc , ISNULL(z.gdcnt,99999) gdcnt , CASE WHEN ISNULL(a.soldoutyn,'N')='N' THEN 'N' ELSE a.soldoutyn END soldoutyn , CASE WHEN ISNULL(a.deliverydtyn,'')='' THEN 'N' ELSE a.deliverydtyn END deliverydtyn , ISNULL(a.newyn,'N') newyn , ISNULL(a.qtysaleyn,'N') qtysaleyn , ISNULL(a.recommendyn,'N') recommendyn , dbo.getMasterPrice(a.gdcd) saleprice , c.cdcode , c.cdname , a.priceyn , i.origin , i.producer , i.address , ISNULL(t.cdname,'미지정') AS gradedesc , '' od_from , '' od_to , '' dlv_from , '' dlv_to , a.thedaysyn , x.slcat , '' odtype2 FROM gd_master a JOIN cd_master c ON c.cdtype = '20' AND c.cdcode = a.div1 JOIN specialsellingl x ON x.slgdcd = a.gdcd LEFT JOIN gd_cnt z ON z.gdcd = a.gdcd LEFT JOIN gd_item i ON i.gdcd = a.gdcd LEFT JOIN cd_master t ON t.cdtype = '09' AND t.cdcode = i.typecd WHERE 1 = 1 AND a.useyn = 'Y' AND a.divcd IN ('30','20') AND x.slidx = #{sidx}
AND ISNULL(a.optionp,'N') = 'N'
UNION ALL SELECT '12' odtype , r.gdcd , r.gdnm gdname , r.unit , m.price1 , m.gdimg , m.mgdimg1 , r.divcd , r.divcd1 div1 , r.indt , r.updt , m.shortdesc , ISNULL(gc.gdcnt,99999) gdcnt , CASE WHEN ISNULL(soldoutyn,'N')='N' THEN 'N' ELSE soldoutyn END soldoutyn , CASE WHEN ISNULL(m.deliverydtyn,'')='' THEN 'N' ELSE m.deliverydtyn END deliverydtyn , ISNULL(m.newyn,'N') newyn , 'N' qtysaleyn , 'N' recommendyn , dbo.getMasterPrice(r.gdcd) saleprice , c.cdcode, c.cdname , 'Y' AS priceyn , i.origin , i.producer , i.address , ISNULL(t.cdname,'미지정') AS gradedesc , r.od_from , r.od_to , r.dlv_from , r.dlv_to , m.thedaysyn , x.slcat , r.type odtype2 FROM od_reservegoods r JOIN gd_master m ON r.gdcd = m.gdcd JOIN cd_master c ON c.cdtype = '20' AND c.cdcode = m.div1 JOIN specialsellingl x ON x.slgdcd = r.gdcd LEFT JOIN gd_item i ON i.gdcd = r.gdcd LEFT JOIN cd_master t ON t.cdtype = '09' AND t.cdcode = i.typecd LEFT JOIN gd_cnt gc ON gc.gdcd = r.gdcd WHERE 1 = 1 AND r.useyn = 'Y'
AND r.od_from <= CONVERT(VARCHAR,GETDATE(),23) AND r.od_to >= CONVERT(VARCHAR,GETDATE(),23)
AND x.slidx = #{sidx}
AND ISNULL(m.optionp,'N') = 'N'
) AS x
LEFT JOIN (SELECT gdcd bgdcd, SUM(gdcnt) bestcnt FROM od_goods WHERE indt >= CONVERT(DATETIME, '" & DATE - 1 & "', 21) GROUP BY gdcd) b ON x.gdcd = b.bgdcd
WHERE 1 = 1
AND x.odtype = #{shltoty}
ORDER BY x.slcat
ORDER BY cdcode
, x.saleprice DESC
, x.saleprice
, x.indt DESC
, b.bestcnt DESC, x.gdcd
