mapper.select:
/* skcncAllProdPaging 기획전 포함 상품 총 페이지 수*/ SELECT CEILING( CAST(COUNT(1) AS FLOAT)/ #{intPagePerItem}) MAXPAGE , COUNT(gdcd) CNT FROM ( SELECT '11' odtype , a.gdcd FROM gd_master a JOIN specialsellingl x ON x.slgdcd = a.gdcd LEFT JOIN od_reservegoods r ON a.gdcd = r.gdcd AND r.useyn = 'Y'
AND DATEDIFF(D,CONVERT(DATETIME,r.od_from,120),GETDATE()) >= 0 AND DATEDIFF(D,GETDATE(),CONVERT(DATETIME,r.od_to,120)) >= 0
WHERE 1 = 1 AND a.useyn = 'Y' AND (a.reserveyn = 'N' OR (a.reserveyn = 'Y' AND r.gdcd IS NOT NULL)) AND a.divcd IN ('30','20') AND x.slidx = #{sidx}
AND a.exdiv1 = #{div1}
x.slcat = #{div1}
AND a.reserveyn = 'Y'
AND a.reserveyn = 'N'
AND ISNULL(a.optionp,'N') = 'N'
) x WHERE 1 = 1

/* ' 상품 정렬 기준 추가 If rs_isud = "Y" Then strListOrderBy = " ORDER BY x.slcat, " Else 'strListOrderBy = " ORDER BY cdcode, " strListOrderBy = " ORDER BY exdiv1, " End If */ SELECT * FROM ( SELECT FLOOR( (ROW_NUMBER() OVER ( #{strListOrderBy} ) - 1 ) / #{intPagePerItem} + 1) AS PAGE , x.* FROM ( SELECT a.gdcd , a.gdname , a.unit , a.price1 , a.gdimg , a.mgdimg1 , a.divcd , a.div1 , a.indt , a.updt , a.shortdesc , ISNULL(z.gdcnt,99999) gdcnt , ISNULL(a.soldoutyn,'N') soldoutyn , ISNULL(a.newyn,'N') newyn , ISNULL(a.qtysaleyn,'N') qtysaleyn , ISNULL(a.recommendyn,'N') recommendyn , dbo.getMasterPrice(a.gdcd) saleprice , a.priceyn , a.exdiv1 , e.catdesc , i.origin , i.producer , i.address , ISNULL(t.cdname,'미지정') AS gradedesc , ISNULL(r.od_from,'') od_from , ISNULL(r.od_to,'') od_to , ISNULL(r.dlv_from,'') dlv_from , ISNULL(r.dlv_to,'') dlv_to , a.thedaysyn , x.slcat , CASE a.dispatchtype WHEN 'I' THEN '11' WHEN 'O' THEN '15' END odtype , ISNULL(a.deliverydtyn,'N') deliverydtyn , ISNULL(a.reserveyn,'N') reserveyn FROM gd_master a JOIN specialsellingl x ON x.slgdcd = a.gdcd LEFT JOIN od_reservegoods r ON a.gdcd = r.gdcd AND r.useyn = 'Y'
AND DATEDIFF(D,CONVERT(DATETIME,r.od_from,120),GETDATE()) >= 0 AND DATEDIFF(D,GETDATE(),CONVERT(DATETIME,r.od_to,120)) >= 0
LEFT JOIN gd_cnt z ON z.gdcd = a.gdcd LEFT JOIN gd_item i ON i.gdcd = a.gdcd LEFT JOIN cd_master t ON t.cdtype = '09' AND t.cdcode = i.typecd LEFT JOIN exmenu e ON a.exdiv1 = e.cat1 WHERE 1 = 1 AND a.useyn = 'Y' AND (a.reserveyn = 'N' OR (a.reserveyn = 'Y' AND r.gdcd IS NOT NULL)) AND a.divcd IN ('30','20') AND x.slidx = #{sidx}
AND a.exdiv1 = #{div1}
AND x.slcat = #{div1}
AND ISNULL(a.optionp,'N') = 'N'
) AS x
LEFT JOIN (SELECT gdcd bgdcd, SUM(gdcnt) bestcnt FROM od_goods WHERE indt >= CONVERT(DATETIME, #{Date} - 1, 21) GROUP BY gdcd) b ON x.gdcd = b.bgdcd
WHERE 1 = 1
AND x.reserveyn = 'Y'
AND x.reserveyn = 'N'
) AS pl WHERE PAGE = #{intPage}
