mapper.update:
/*updateDIValue 둘 이상의 아이디에서 선택한 고객코드로 등록된 휴대전화번호로 인증을 마쳤으므로 DI값 반영한다.*/
UPDATE mb_master
SET pcert_reqno = #{strReqNo}
  , pcert_di = #{strPidNo}
 WHERE memcd = #{strMemCd}

mapper.select:
  /* getMemId */
SELECT memid
     , CONVERT(CHAR(7),indt,120) indt
FROM mb_master
WHERE memcd = #{strMemCd}
